mapper.select:
SELECT guild_name, user_id, board_title, board_content, view_cnt, date_format(reg_date, '%y-%m-%d') AS reg_date 
FROM guild_boards
ORDER BY reg_date DESC;

SELECT *
FROM guild_boards
WHERE guild_name = #{guildName} AND user_id = #{userId} AND reg_date = #{regDate};

SELECT guild_name, user_id, board_title, board_content, view_cnt, date_format(reg_date, '%y-%m-%d') AS reg_date
FROM guild_boards
WHERE guild_name = #{guildName};
ORDER BY reg_date DESC;

SELECT guild_name, user_id, board_title, board_content, view_cnt, date_format(reg_date, '%y-%m-%d') AS reg_date
FROM guild_boards
WHERE user_id = #{userId};
ORDER BY reg_date DESC;

SELECT guild_name, user_id, board_title, board_content, view_cnt, date_format(reg_date, '%y-%m-%d') AS reg_date
FROM guild_boards
WHERE guild_name = #{guildName} AND user_id = #{userId};
ORDER BY reg_date DESC;

mapper.update:
UPDATE guild_boards
SET view_cnt = view_cnt+1
WHERE guild_name = #{guildName} AND user_id = #{userId} AND reg_date = #{regDate};

UPDATE guild_boards
SET comment_content = #{commentContent}, reg_date = now()
WHERE guild_name = #{guildName} AND user_id = #{userId} AND reg_date = #{regDate};
