mapper.select:
SELECT review_id, user_id, video_id, video_title, channel_name, comment_content, view_cnt, date_format(reg_date, '%y-%m-%d') AS reg_date 
FROM comment
ORDER BY review_id DESC;

SELECT review_id, user_id, video_id, video_title, channel_name, comment_content, view_cnt, date_format(reg_date, '%y-%m-%d') AS reg_date
FROM comment
WHERE user_id = #{userId} AND reg_date = #{regDate};

mapper.update:
UPDATE comment
SET view_cnt = view_cnt+1
WHERE user_id = #{userId} AND reg_date = #{regDate};

UPDATE comment
SET comment_content = #{commentContent}, reg_date = now()
WHERE user_id = #{userId} AND reg_date = #{regDate};

mapper.select:
SELECT review_id, user_id, video_id, video_title, channel_name, comment_content, view_cnt, date_format(reg_date, '%y-%m-%d') AS reg_date FROM comment
WHERE ${key} LIKE concat('%', #{word}, '%')
ORDER BY ${orderBy} ${orderByDir}
